import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Kayit.css';

const languages = {
  en: 'English',
  ru: 'русский',
  tr: 'Türkçe',
  fr: 'Français',
  ar: 'العربية',
  zh: '中文',
  de: 'Deutsch',
  kk: 'Қазақ',
  az: 'Azərbaycan'
};

const languagePrompts = {
  en: 'Choose your language',
  ru: 'Выберите ваш язык',
  tr: 'Dil Seçiniz',
  fr: 'Choisissez votre langue',
  ar: 'اختر لغتك',
  zh: '选择你的语言',
  de: 'Wähle deine Sprache',
  kk: 'Тіліңізді таңдаңыз',
  az: 'Dil seçin'
};

const Kayit = ({ onSelectLanguage }) => {
  const [currentLanguage, setCurrentLanguage] = useState(languagePrompts.en);
  const languageKeys = Object.keys(languages);
  let languageIndex = 0;
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLanguage(languagePrompts[languageKeys[languageIndex]]);
      languageIndex = (languageIndex + 1) % languageKeys.length;
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const handleLanguageSelect = (language) => {
    localStorage.setItem('selectedLanguage', language);
    onSelectLanguage(language); // Send the selected language to the parent component
    navigate('/main', { state: { language } }); // Navigate to MainPage with the selected language
  };

  const backgroundImage = window.innerWidth <= 768 ? '/kayit/Kayıtt.png' : '/kayit/Arkaplan.png';

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <img src="/kayit/logoo.png" alt="Logo" className="logo" />
      <div className="current-language">{currentLanguage}</div>
      <div className="language-container">
        {languageKeys.slice(0, 8).map((key) => (
          <button
            key={key}
            className="language-button"
            onClick={() => handleLanguageSelect(key)}
          >
            <span className="language-text">{languages[key]}</span>
          </button>
        ))}
      </div>
      <div className="extra-language-button">
        <button
          className="language-button"
          onClick={() => handleLanguageSelect(languageKeys[8])}
        >
          <span className="language-text">{languages[languageKeys[8]]}</span>
        </button>
      </div>
    </div>
  );
};

export default Kayit;
