import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useLocation } from 'react-router-dom';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const SurahDetail = () => {
  const { surahNumber } = useParams();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const type = queryParams.get('type');
  const [surah, setSurah] = useState(null);
  const [ayahs, setAyahs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentAyah, setCurrentAyah] = useState(null);
  const [audioError, setAudioError] = useState(false);

  useEffect(() => {
    const fetchSurahDetails = async () => {
      try {
        const surahResponse = await axios.get(`https://api.quran.com/api/v4/chapters/${surahNumber}`);
        setSurah(surahResponse.data.chapter);

        if (type === 'original') {
          const ayahsResponse = await axios.get(`https://api.quran.com/api/v4/quran/verses/by_chapter/${surahNumber}`);
          setAyahs(ayahsResponse.data.verses);
        } else if (type === 'translation') {
          const ayahsResponse = await axios.get(`https://api.quran.com/api/v4/quran/translations/131/by_chapter/${surahNumber}`);
          setAyahs(ayahsResponse.data.translations);
        }

        setLoading(false);
      } catch (error) {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
      }
    };

    fetchSurahDetails();
  }, [surahNumber, type]);

  const playAyahAudio = (ayahNumber) => {
    setAudioError(false);
    axios.get(`https://api.quran.com/api/v4/quran/verses/by_key/${surahNumber}:${ayahNumber}?audio=1`)
      .then(response => {
        const audioUrl = response.data.verses[0]?.audio.url;
        if (audioUrl) {
          setCurrentAyah(audioUrl);
        } else {
          setAudioError(true);
        }
      })
      .catch(error => {
        console.error('Ses dosyası alınırken hata oluştu!', error);
        setAudioError(true);
      });
  };

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  return (
    <div>
      <h1>{surah.name_simple}</h1>
      <p>{surah.translated_name.name}</p>
      <ul>
        {ayahs.map(ayah => (
          <li key={ayah.verse_key || ayah.id}>
            {type === 'original' ? ayah.text_uthmani : ayah.text}
            {type === 'original' && <button onClick={() => playAyahAudio(ayah.verse_number)}>Ses Dosyasını Çal</button>}
          </li>
        ))}
      </ul>
      {audioError && <p>Bu ayet için ses dosyası bulunamadı.</p>}
      {currentAyah && (
        <AudioPlayer
          autoPlay
          src={currentAyah}
          onEnded={() => setCurrentAyah(null)}
        />
      )}
    </div>
  );
};

export default SurahDetail;
