import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useLocation } from 'react-router-dom';

const SurahTranslation = () => {
  const { surahNumber } = useParams();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const language = queryParams.get('language');
  const [surah, setSurah] = useState(null);
  const [ayahs, setAyahs] = useState([]);
  const [loading, setLoading] = useState(true);

  const getTranslationEndpoint = (language) => {
    const endpoints = {
      tr: 'tr.altinkaya',
      en: 'en.sahih',
      fr: 'fr.hamidullah',
      ru: 'ru.kuliev'
    };
    return endpoints[language];
  };

  useEffect(() => {
    const fetchSurahDetails = async () => {
      try {
        const surahResponse = await axios.get(`https://api.alquran.cloud/v1/surah/${surahNumber}`);
        setSurah(surahResponse.data.data);

        const translationEndpoint = getTranslationEndpoint(language);
        const ayahsResponse = await axios.get(`https://api.alquran.cloud/v1/surah/${surahNumber}/${translationEndpoint}`);
        setAyahs(ayahsResponse.data.data.ayahs);

        setLoading(false);
      } catch (error) {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
      }
    };

    fetchSurahDetails();
  }, [surahNumber, language]);

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  return (
    <div>
      <h1>{surah.englishName}</h1>
      <p>{surah.name}</p>
      <ul>
        {ayahs.map(ayah => (
          <li key={ayah.number}>
            <p>{ayah.numberInSurah}</p>
            <p>{ayah.text}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SurahTranslation;
