import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Kayit.css';

const languages = {
  en: 'Choose your language',
  ru: 'Выберите ваш язык',
  tr: 'Dil Seçiniz',
  fr: 'Choisissez votre langue',
  ar: 'اختر لغتك',
  zh: '选择你的语言',
  de: 'Wähle deine Sprache',
  kk: 'Тіліңізді таңдаңыз',
  az: 'Dil seçin'
};

const Kayit = ({ onSelectLanguage }) => {
  const [currentLanguage, setCurrentLanguage] = useState(languages.en);
  const languageKeys = Object.keys(languages);
  let languageIndex = 0;
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLanguage(languages[languageKeys[languageIndex]]);
      languageIndex = (languageIndex + 1) % languageKeys.length;
    }, 1000); // Daha hızlı dil döngüsü için 1000ms (1 saniye)

    return () => clearInterval(interval);
  }, []);

  const handleLanguageSelect = (language) => {
    localStorage.setItem('selectedLanguage', language);
    onSelectLanguage(); // Dil seçimi yapıldığında çağrılır
    navigate('/main');
  };

  const isMobile = window.innerWidth <= 768;
  const backgroundImage = isMobile ? '/kayit/Kayıt.png' : '/kayit/Arkaplan.png';

  const containerStyle = {
    width: '100%',
    height: '100vh',
    position: 'relative',
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column',
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center', // Metin ortalamak için
    padding: '10px' // Mobil cihazlarda kenarlardan boşluk bırakmak için
  };

  return (
    <div style={containerStyle}>
      <div className="current-language">{currentLanguage}</div>
      <div className="language-container">
        <button className="language-button" onClick={() => handleLanguageSelect('tr')}>
          <span className="language-text">Türkçe</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('en')}>
          <span className="language-text">English</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('fa')}>
          <span className="language-text">فارسي</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('fr')}>
          <span className="language-text">Français</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('ru')}>
          <span className="language-text">русский</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('zh')}>
          <span className="language-text">中文</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('de')}>
          <span className="language-text">Deutsch</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('kk')}>
          <span className="language-text">Қазақ</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('az')}>
          <span className="language-text">Azərbaycan</span>
        </button>
      </div>
    </div>
  );
};

export default Kayit;
