import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SurahList2 = () => {
  const [surahs, setSurahs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('https://api.quran.com/api/v4/chapters')
      .then(response => {
        setSurahs(response.data.chapters);
      })
      .catch(error => {
        console.error('Sureler alınırken hata oluştu!', error);
      });
  }, []);

  const handleSelection = (surahNumber, type) => {
    if (type === 'original') {
      navigate(`/surah/${surahNumber}`);
    } else {
      navigate(`/surah-translation/${surahNumber}`);
    }
  };

  return (
    <div>
      <h1>Kuran Sureleri</h1>
      <ul>
        {surahs.map(surah => (
          <li key={surah.id}>
            <span>{surah.name_simple}</span>
            <button onClick={() => handleSelection(surah.id, 'original')}>Orijinal</button>
            <button onClick={() => handleSelection(surah.id, 'translation')}>Meal</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SurahList2;
