import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Kayit from './components/Kayit';
import WalletConnector from './components/WalletConnector';
import PrayerTimes from './components/PrayerTimes';
import DailyPrayers from './components/DailyPrayers';
import Hadiths from './components/Hadiths';
import Dhikr from './components/Dhikr';
import NamesOfAllah from './components/NameOfAllah';
import QuranVerses from './components/QuranVerses';
import Sacrifice from './components/Sacrifice';
import NonVisualization from './components/NonVisualization';
import DAO from './components/DAO';
import MainPage from './components/MainPage';
import './App.css';

function App() {
  const [languageSelected, setLanguageSelected] = useState(false);
  const [walletConnected, setWalletConnected] = useState(false);

  const handleLanguageSelection = () => {
    setLanguageSelected(true);
  };

  const handleWalletConnection = () => {
    setWalletConnected(true);
  };

  return (
    <Router>
      <div className="App">
        <Header />
        <Switch>
          <Route path="/k" component={() => <Kayit onSelectLanguage={handleLanguageSelection} />} />
          <Route path="/wallet" component={() => <WalletConnector onConnectWallet={handleWalletConnection} />} />
          {languageSelected && walletConnected && <Redirect from="/" to="/main" />}
          {!languageSelected && <Redirect from="/" to="/kayit" />}
          {languageSelected && !walletConnected && <Redirect from="/" to="/wallet" />}
          <Route path="/main" component={MainPage} />
          <Route path="/prayer-times" component={PrayerTimes} />
          <Route path="/daily-prayers" component={DailyPrayers} />
          <Route path="/hadiths" component={Hadiths} />
          <Route path="/dhikr" component={Dhikr} />
          <Route path="/names-of-allah" component={NamesOfAllah} />
          <Route path="/quran-verses" component={QuranVerses} />
          <Route path="/sacrifice" component={Sacrifice} />
          <Route path="/non-visualization" component={NonVisualization} />
          <Route path="/dao" component={DAO} />
        </Switch>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
