import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { TonConnectUIProvider, THEME } from "@tonconnect/ui-react";
import WalletConnector from './components/WalletConnector'; // Doğru import
import Kayit from './components/Kayit'; // Doğru import
import MainPage from './components/MainPage'; // Doğru import
import PrayerTimes from './components/PrayerTimes'; // Doğru import
import DailyPrayers from './components/DailyPrayers'; // Doğru import
import Hadiths from './components/Hadiths'; // Doğru import
import Dhikr from './components/Dhikr'; // Doğru import
import NamesOfAllah from './components/AllahIsimleri'; // Doğru import
import Sacrifice from './components/Sacrifice'; // Doğru import
import NonVisualization from './components/NonVisualization'; // Doğru import
import DAO from './components/DAO'; // Doğru import
import SurahList2 from './components/SurahList2'; // Doğru import
import SurahTranslation from './components/SurahTranslation'; // Doğru import
import SurahOriginal from './components/SurahOriginal'; // Doğru import
import ProfilePage from './components/ProfilePage'; // Doğru import
import NavBar from './components/Navbar'; // Doğru import
import ToDoList from './components/ToDoList'; // Doğru import
import NewPage from './components/NewPage'; // Doğru import
import CalendarPage from './components/Calendar'; // Doğru import
import SelectionPage from './components/SelectionPage'; // Doğru import
import KiblaPage from './components/KiblaPage'; // Doğru import
import SelectionPage2 from './components/SelectionPage21'; // Doğru import
import './App.css';

function App() {
  const [walletConnected, setWalletConnected] = useState(false);
  const [languageSelected, setLanguageSelected] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('en');
  const [data, setData] = useState(null);

  useEffect(() => {
    const walletStatus = localStorage.getItem('walletConnected') === 'true';
    const language = localStorage.getItem('selectedLanguage');
    setWalletConnected(walletStatus);
    setLanguageSelected(language !== null);
    setSelectedLanguage(language || 'en');

    // Fetch data from the server
    axios.get('http://localhost:3000/')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Network error:', error);
      });

    // Set Telegram webhook
    const botToken = '7309348405:AAEJmW2iw5zLkbhuEFg0kMlQIxyFpcEaZ0M'; // Your actual bot token
    const setWebhook = async () => {
      const url = `https://api.telegram.org/bot${botToken}/setWebhook?url=https://main--lustrous-mousse-6b5222.netlify.app/`; // Your actual app URL
      try {
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
      } catch (error) {
        console.error('Error setting webhook:', error);
      }
    };

    setWebhook();
  }, []);

  return (
    <TonConnectUIProvider
      manifestUrl="https://ton-connect.github.io/demo-dapp-with-wallet/tonconnect-manifest.json"
      uiPreferences={{ theme: THEME.DARK }}
    >
      <Router>
        <AppWithRouter
          walletConnected={walletConnected}
          languageSelected={languageSelected}
          selectedLanguage={selectedLanguage}
          setWalletConnected={setWalletConnected}
          setSelectedLanguage={setSelectedLanguage}
          data={data}
        />
      </Router>
    </TonConnectUIProvider>
  );
}

function AppWithRouter({
  walletConnected,
  languageSelected,
  selectedLanguage,
  setWalletConnected,
  setSelectedLanguage,
  data
}) {
  const navigate = useNavigate();

  // Handle wallet connection and navigate to MainPage
  const handleWalletConnection = () => {
    setWalletConnected(true);
    localStorage.setItem('walletConnected', 'true');
    navigate('/main', { state: { language: selectedLanguage } });
  };

  // Handle language selection and save it to local storage
  const handleLanguageSelection = (language) => {
    setSelectedLanguage(language);
    localStorage.setItem('selectedLanguage', language);
  };

  return (
    <div className="App">
      {walletConnected && languageSelected && <NavBar />}
      <Routes>
        <Route path="/wallet" element={<WalletConnector onConnectWallet={handleWalletConnection} selectedLanguage={selectedLanguage} />} />
        <Route path="/kayit" element={<Kayit onSelectLanguage={handleLanguageSelection} />} />
        <Route path="/main" element={<MainPage />} />
        <Route path="/prayer-times" element={<PrayerTimes />} />
        <Route path="/daily-prayers" element={<DailyPrayers />} />
        <Route path="/hadiths" element={<Hadiths />} />
        <Route path="/dhikr" element={<Dhikr />} />
        <Route path="/names-of-allah" element={<NamesOfAllah />} />
        <Route path="/quran-verses" element={<SurahList2 />} />
        <Route path="/surah-translation/:surahNumber" element={<SurahTranslation />} />
        <Route path="/sacrifice" element={<Sacrifice />} />
        <Route path="/non-visualization" element={<NonVisualization />} />
        <Route path="/dao" element={<DAO />} />
        <Route path="/surah-original/:surahNumber" element={<SurahOriginal />} />
        <Route path="/profile" element={<ProfilePage />} />
        <Route path="/todo-list" element={<ToDoList />} />
        <Route path="/new-page" element={<NewPage />} />
        <Route path="/calendar" element={<CalendarPage />} />
        <Route path="/selection" element={<SelectionPage />} />
        <Route path="/kibla" element={<KiblaPage />} />
        <Route path="/selection21" element={<SelectionPage2 />} />
        <Route
          path="/"
          element={
            !languageSelected ? (
              <Navigate to="/kayit" />
            ) : !walletConnected ? (
              <Navigate to="/wallet" />
            ) : (
              <Navigate to="/main" />
            )
          }
        />
      </Routes>
      <header className="App-header">
        <p>{data}</p>
      </header>
    </div>
  );
}

export default App;
