import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Kayit.css';

const Kayit = () => {
  const [currentLanguage, setCurrentLanguage] = useState('Choose your language');
  const languages = [
    'Choose your language',
    'Выберите ваш язык',
    'Dil Seçiniz',
    'Choisissez votre langue',
    'اختر لغتك'
  ];
  let languageIndex = 0;
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLanguage(languages[languageIndex]);
      languageIndex = (languageIndex + 1) % languages.length;
    }, 300);

    return () => clearInterval(interval);
  }, []);

  const handleLanguageSelect = (language) => {
    localStorage.setItem('selectedLanguage', language);
    navigate('/quran-verses');
  };

  const isMobile = window.innerWidth <= 768;
  const backgroundImage = isMobile ? '/kayit/Kayıt.png' : '/kayit/Arkaplan.png';

  const containerStyle = {
    width: '100%',
    height: '100vh',
    position: 'relative',
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column',
    fontFamily: 'Arial, sans-serif',
  };

  return (
    <div style={containerStyle}>
      <div className="language-container">
        <button className="language-button" onClick={() => handleLanguageSelect('tr')}>
          <span className="language-text">Türkçe</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('en')}>
          <span className="language-text">English</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('fa')}>
          <span className="language-text">فارسي</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('fr')}>
          <span className="language-text">Français</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('ru')}>
          <span className="language-text">русский</span>
        </button>
      </div>
      <div className="current-language">{currentLanguage}</div>
    </div>
  );
};

export default Kayit;
