import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MainPage.css';

const MainPage = () => {
  const [verse, setVerse] = useState(null);
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [dailyPrayer, setDailyPrayer] = useState(null);
  const [hadith, setHadith] = useState(null);
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDailyVerse = async () => {
      try {
        const response = await axios.get('https://api.alquran.cloud/v1/ayah/random');
        console.log('Verse Response:', response.data);
        setVerse(response.data.data);
      } catch (error) {
        setError('Ayet alınırken hata oluştu!');
        console.error('Verse Error:', error);
      }
    };

    const fetchLocation = async () => {
      try {
        const response = await axios.get('https://ipapi.co/json/');
        console.log('Location Response:', response.data);
        setLocation(response.data);
      } catch (error) {
        setError('Konum alınırken hata oluştu!');
        console.error('Location Error:', error);
      }
    };

    const fetchDailyPrayer = async () => {
      try {
        const response = await axios.get('https://islamic-api-zhirrr.vercel.app/api/quotes/duas');
        console.log('Daily Prayer Response:', response.data);
        setDailyPrayer(response.data.data[0].text); // Örnek olarak ilk duayı alıyoruz
      } catch (error) {
        setError('Günlük dua alınırken hata oluştu!');
        console.error('Daily Prayer Error:', error);
      }
    };

    const fetchHadith = async () => {
      try {
        const response = await axios.get('https://api.hadith.sutanlab.id/random');
        console.log('Hadith Response:', response.data);
        setHadith(response.data.data.contents.arab); // Hadisi Arapça olarak alıyoruz
      } catch (error) {
        setError('Hadis alınırken hata oluştu!');
        console.error('Hadith Error:', error);
      }
    };

    fetchDailyVerse();
    fetchLocation();
    fetchDailyPrayer();
    fetchHadith();
  }, []);

  useEffect(() => {
    const fetchPrayerTimes = async (city, country) => {
      try {
        const response = await axios.get(`http://api.aladhan.com/v1/timingsByCity?city=${city}&country=${country}&method=2`);
        console.log('Prayer Times Response:', response.data);
        setPrayerTimes(response.data.data.timings);
      } catch (error) {
        setError('Namaz vakitleri alınırken hata oluştu!');
        console.error('Prayer Times Error:', error);
      }
    };

    if (location) {
      fetchPrayerTimes(location.city, location.country_name);
    }
  }, [location]);

  return (
    <div className="main-page">
      {error && <p>{error}</p>}
      <div className="daily-verse">
        <h2>Günün Ayeti</h2>
        {verse ? <p>{verse.text}</p> : <p>Yükleniyor...</p>}
      </div>
      <div className="prayer-times">
        <h2>Namaz Vakitleri</h2>
        {prayerTimes ? (
          <ul>
            {Object.entries(prayerTimes).map(([name, time]) => (
              <li key={name}>{name}: {time}</li>
            ))}
          </ul>
        ) : <p>Yükleniyor...</p>}
      </div>
      <div className="daily-prayer">
        <h2>Günlük Dua</h2>
        {dailyPrayer ? <p>{dailyPrayer}</p> : <p>Yükleniyor...</p>}
      </div>
      <div className="hadith">
        <h2>Hadis</h2>
        {hadith ? <p>{hadith}</p> : <p>Yükleniyor...</p>}
      </div>
    </div>
  );
};

export default MainPage;
