const MainPage = () => {
    const [verse, setVerse] = useState(null);
    const [prayerTimes, setPrayerTimes] = useState(null);
    const [dailyPrayer, setDailyPrayer] = useState(null);
    const [hadith, setHadith] = useState(null);
    const [location, setLocation] = useState(null);
    const [error, setError] = useState(null);
  
    useEffect(() => {
      const fetchDailyVerse = async () => {
        try {
          const response = await axios.get('https://api.quran.com/api/v4/verses/random?language=tr');
          setVerse(response.data.verse);
        } catch (error) {
          setError('Ayet alınırken hata oluştu!');
          console.error(error);
        }
      };
  
      const fetchLocation = async () => {
        try {
          const response = await axios.get('https://ipapi.co/json/');
          setLocation(response.data);
        } catch (error) {
          setError('Konum alınırken hata oluştu!');
          console.error(error);
        }
      };
  
      const fetchDailyPrayer = async () => {
        try {
          const response = await axios.get('https://api.pray.zone/v2/times/today.json?city=Istanbul');
          setDailyPrayer(response.data.results.datetime[0].times.Dhuhr); // Örnek olarak öğle namazı vakti
        } catch (error) {
          setError('Günlük dua alınırken hata oluştu!');
          console.error(error);
        }
      };
  
      const fetchHadith = async () => {
        try {
          const response = await axios.get('https://api.hadith.sunnah.com/hadiths/random'); // Bu örnektir, gerçekte kullanmanız gereken URL olabilir.
          setHadith(response.data.hadith[0].text);
        } catch (error) {
          setError('Hadis alınırken hata oluştu!');
          console.error(error);
        }
      };
  
      fetchDailyVerse();
      fetchLocation();
      fetchDailyPrayer();
      fetchHadith();
    }, []);
  
    useEffect(() => {
      const fetchPrayerTimes = async (city, country) => {
        try {
          const response = await axios.get(`https://api.aladhan.com/v1/timingsByCity?city=${city}&country=${country}&method=2`);
          setPrayerTimes(response.data.data.timings);
        } catch (error) {
          setError('Namaz vakitleri alınırken hata oluştu!');
          console.error(error);
        }
      };
  
      if (location) {
        fetchPrayerTimes(location.city, location.country_name);
      }
    }, [location]);
  
    return (
      <div className="main-page">
        {error && <p>{error}</p>}
        <div className="daily-verse">
          <h2>Günün Ayeti</h2>
          {verse ? <p>{verse.text}</p> : <p>Yükleniyor...</p>}
        </div>
        <div className="prayer-times">
          <h2>Namaz Vakitleri</h2>
          {prayerTimes ? (
            <ul>
              {Object.entries(prayerTimes).map(([name, time]) => (
                <li key={name}>{name}: {time}</li>
              ))}
            </ul>
          ) : <p>Yükleniyor...</p>}
        </div>
        <div className="daily-prayer">
          <h2>Günlük Dua</h2>
          {dailyPrayer ? <p>{dailyPrayer}</p> : <p>Yükleniyor...</p>}
        </div>
        <div className="hadith">
          <h2>Hadis</h2>
          {hadith ? <p>{hadith}</p> : <p>Yükleniyor...</p>}
        </div>
      </div>
    );
  };
  
  export default MainPage;
