import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const SurahDetail = () => {
  const { surahNumber } = useParams();
  const [surah, setSurah] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentAyah, setCurrentAyah] = useState(null);
  const [audioError, setAudioError] = useState(false);

  useEffect(() => {
    // Sure detaylarını almak için API çağrısı
    axios.get(`https://api.alquran.cloud/v1/surah/${surahNumber}`)
      .then(response => {
        setSurah(response.data.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
      });
  }, [surahNumber]);

  const playAyahAudio = (ayahNumber) => {
    setAudioError(false);
    // Ayet ses dosyasını almak için API çağrısı
    axios.get(`https://api.alquran.cloud/v1/ayah/${surahNumber}:${ayahNumber}/ar.alafasy`)
      .then(response => {
        const audioUrl = response.data.data.audio;
        if (audioUrl) {
          setCurrentAyah(audioUrl);
        } else {
          setAudioError(true);
        }
      })
      .catch(error => {
        console.error('Ses dosyası alınırken hata oluştu!', error);
        setAudioError(true);
      });
  };

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  return (
    <div>
      <h1>{surah.englishName}</h1>
      <p>{surah.englishNameTranslation}</p>
      <ul>
        {surah.ayahs.map(ayah => (
          <li key={ayah.number}>
            {ayah.text}
            <button onClick={() => playAyahAudio(ayah.number)}>Ses Dosyasını Çal</button>
          </li>
        ))}
      </ul>
      {audioError && <p>Bu ayet için ses dosyası bulunamadı.</p>}
      {currentAyah && (
        <AudioPlayer
          autoPlay
          src={currentAyah}
          onEnded={() => setCurrentAyah(null)}
        />
      )}
    </div>
  );
};

export default SurahDetail;
