import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const SurahDetail = () => {
  const { surahNumber } = useParams();
  const [surah, setSurah] = useState(null);
  const [ayahs, setAyahs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentAyah, setCurrentAyah] = useState(null);
  const [audioError, setAudioError] = useState(false);

  useEffect(() => {
    axios.get(`https://cdn.jsdelivr.net/gh/fawazahmed0/quran-api@1/editions/ara-quran-${surahNumber}.json`)
      .then(response => {
        setSurah(response.data.chapter);
        setAyahs(response.data.chapter.verses);
        setLoading(false);
      })
      .catch(error => {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
      });
  }, [surahNumber]);

  const playAyahAudio = (audioUrl) => {
    setAudioError(false);
    if (audioUrl) {
      setCurrentAyah(audioUrl);
    } else {
      setAudioError(true);
    }
  };

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  return (
    <div>
      <h1>{surah.name}</h1>
      <ul>
        {ayahs.map((ayah, index) => (
          <li key={index}>
            {ayah.text}
            <button onClick={() => playAyahAudio(ayah.audio)}>Ses Dosyasını Çal</button>
          </li>
        ))}
      </ul>
      {audioError && <p>Bu ayet için ses dosyası bulunamadı.</p>}
      {currentAyah && (
        <AudioPlayer
          autoPlay
          src={currentAyah}
          onEnded={() => setCurrentAyah(null)}
        />
      )}
    </div>
  );
};

export default SurahDetail;
