import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const SurahDetail = () => {
  const { surahNumber } = useParams();
  const [surah, setSurah] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentAyah, setCurrentAyah] = useState(null);

  useEffect(() => {
    axios.get(`https://api.alquran.cloud/v1/surah/${surahNumber}`)
      .then(response => {
        setSurah(response.data.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('There was an error fetching the data!', error);
        setLoading(false);
      });
  }, [surahNumber]);

  const playAyahAudio = (ayahNumber) => {
    axios.get(`https://api.alquran.cloud/v1/ayah/${surahNumber}:${ayahNumber}/ar.alafasy`)
      .then(response => {
        setCurrentAyah(response.data.data.audio);
      })
      .catch(error => {
        console.error('There was an error fetching the audio!', error);
      });
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!surah) {
    return <p>Surah not found</p>;
  }

  return (
    <div>
      <h1>{surah.englishName}</h1>
      <p>{surah.englishNameTranslation}</p>
      <ul>
        {surah.ayahs.map(ayah => (
          <li key={ayah.number}>
            {ayah.text}
            <button onClick={() => playAyahAudio(ayah.number)}>Play Audio</button>
          </li>
        ))}
      </ul>
      {currentAyah && (
        <AudioPlayer
          autoPlay
          src={currentAyah}
          onEnded={() => setCurrentAyah(null)}
        />
      )}
    </div>
  );
};

export default SurahDetail;
