import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '/Kayit.css'; // CSS dosyasını içe aktarıyoruz

const Kayit = ({ onSelectLanguage }) => {
  const [currentLanguage, setCurrentLanguage] = useState('Choose your language');
  const languages = [
    'Choose your language',
    'Выберите ваш язык',
    'Dil Seçiniz',
    'Choisissez votre langue',
    'اختر لغتك'
  ];
  let languageIndex = 0;
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLanguage(languages[languageIndex]);
      languageIndex = (languageIndex + 1) % languages.length;
    }, 300);

    return () => clearInterval(interval);
  }, []);

  const handleLanguageSelect = (language) => {
    onSelectLanguage();
    navigate('/main', { state: { language } });
  };

  return (
    <div className="kayit-container">
      <div className="language-container">
        <button className="language-button" onClick={() => handleLanguageSelect('Türkçe')}>
          <span className="language-text">Türkçe</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('English')}>
          <span className="language-text">English</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('فارسي')}>
          <span className="language-text">فارسي</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('Français')}>
          <span className="language-text">Français</span>
        </button>
        <button className="language-button" onClick={() => handleLanguageSelect('русский')}>
          <span className="language-text">русский</span>
        </button>
      </div>
      <div className="current-language">{currentLanguage}</div>
    </div>
  );
};

export default Kayit;
