import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const SurahDetail = () => {
  const { surahNumber } = useParams();
  const [surah, setSurah] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [loading, setLoading] = useState(true);
  const [audioError, setAudioError] = useState(false);

  useEffect(() => {
    axios.get(`https://api.quran.com/api/v4/chapters/${surahNumber}`)
      .then(response => {
        setSurah(response.data.chapter);
        return axios.get(`https://api.quran.com/api/v4/chapter_recitations/1/${surahNumber}`);
      })
      .then(response => {
        setAudioUrl(response.data.audio_file.audio_url);
        setLoading(false);
      })
      .catch(error => {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
        setAudioError(true);
      });
  }, [surahNumber]);

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  return (
    <div>
      <h1>{surah.name_simple}</h1>
      <p>{surah.translated_name.name}</p>
      {audioError && <p>Bu sure için ses dosyası bulunamadı.</p>}
      {audioUrl && (
        <AudioPlayer
          autoPlay
          src={audioUrl}
          onEnded={() => setAudioUrl(null)}
        />
      )}
    </div>
  );
};

export default SurahDetail;
