import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
  const [ayah, setAyah] = useState('');
  const [surah, setSurah] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Alquran Cloud API'si örnek kullanımı
    axios.get('https://api.alquran.cloud/v1/ayah/262')
      .then(response => {
        setAyah(response.data.data.text);
        setSurah(response.data.data.surah.englishName);
        setLoading(false);
      })
      .catch(error => {
        console.error('There was an error fetching the data!', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>Quran Ayah</h1>
      <p>{ayah}</p>
      <p>Surah: {surah}</p>
    </div>
  );
};

export default App;
