import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useLocation } from 'react-router-dom';

const translationIds = {
  en: 131, // English translation ID
  fr: 149, // French translation ID
  ru: 127, // Russian translation ID
  tr: 95  // Turkish translation ID
};

const cleanText = (text) => {
  return text.replace(/<[^>]*>?/gm, '');
};

const SurahTranslation = () => {
  const { surahNumber } = useParams();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const language = queryParams.get('language');
  const translationId = translationIds[language];
  const [surah, setSurah] = useState(null);
  const [ayahs, setAyahs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSurahDetails = async () => {
      try {
        const surahResponse = await axios.get(`https://api.quran.com/api/v4/chapters/${surahNumber}`);
        setSurah(surahResponse.data.chapter);

        const ayahsResponse = await axios.get(`https://api.quran.com/api/v4/quran/translations/${translationId}?chapter_number=${surahNumber}`);
        const cleanedAyahs = ayahsResponse.data.translations.map(ayah => ({
          ...ayah,
          text: cleanText(ayah.text)
        }));
        setAyahs(cleanedAyahs);

        setLoading(false);
      } catch (error) {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
      }
    };

    fetchSurahDetails();
  }, [surahNumber, translationId]);

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  return (
    <div>
      <h1>{surah.name_simple}</h1>
      <p>{surah.translated_name.name}</p>
      <ul>
        {ayahs.map(ayah => (
          <li key={ayah.verse_key}>
            <p>{ayah.verse_key}</p>
            <p>{ayah.text}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SurahTranslation;
