import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  ImageBackground,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Picker,
} from 'react-native';
import axios from 'axios';

const translations = {
  en: {
    welcome: 'Welcome',
    dailyVerse: 'Daily Verse',
    prayerTimes: 'Prayer Times',
    randomHadith: 'Random Hadith'
  },
  tr: {
    welcome: 'Hoşgeldiniz',
    dailyVerse: 'Günlük Ayet',
    prayerTimes: 'Namaz Vakitleri',
    randomHadith: 'Rastgele Hadis'
  },
  ru: {
    welcome: 'Добро пожаловать',
    dailyVerse: 'Ежедневный стих',
    prayerTimes: 'Время молитвы',
    randomHadith: 'Случайный хадис'
  },
  fr: {
    welcome: 'Bienvenue',
    dailyVerse: 'Verset Quotidien',
    prayerTimes: 'Heures de Prière',
    randomHadith: 'Hadith Aléatoire'
  },
  ar: {
    welcome: 'مرحبا',
    dailyVerse: 'آية اليوم',
    prayerTimes: 'أوقات الصلاة',
    randomHadith: 'حديث عشوائي'
  },
  zh: {
    welcome: '欢迎',
    dailyVerse: '每日诗句',
    prayerTimes: '祷告时间',
    randomHadith: '随机圣训'
  },
  de: {
    welcome: 'Willkommen',
    dailyVerse: 'Täglicher Vers',
    prayerTimes: 'Gebetszeiten',
    randomHadith: 'Zufälliger Hadith'
  },
  kk: {
    welcome: 'Қош келдіңіз',
    dailyVerse: 'Күнделікті аят',
    prayerTimes: 'Намаз уақыты',
    randomHadith: 'Кездейсоқ хадис'
  },
  az: {
    welcome: 'Xoş gəldiniz',
    dailyVerse: 'Gündəlik Ayə',
    prayerTimes: 'Namaz Vaxtları',
    randomHadith: 'Təsadüfi Hədis'
  }
};

export default function App() {
  const [originalVerse, setOriginalVerse] = useState(null);
  const [translatedVerse, setTranslatedVerse] = useState(null);
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [hadith, setHadith] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [error, setError] = useState(null);
  const [language, setLanguage] = useState('en');

  useEffect(() => {
    const fetchDailyVerse = async () => {
      try {
        const response = await axios.get('https://api.alquran.cloud/v1/ayah/random/ar');
        const verseText = response.data.data.text;
        setOriginalVerse(verseText);

        const translatedResponse = await axios.post('http://localhost:5000/translate', {
          text: verseText,
          target_lang: language
        });
        setTranslatedVerse(translatedResponse.data.text);
      } catch (error) {
        setError('Error fetching verse or translation');
        console.error('Verse/Translation Error:', error);
      }
    };

    const fetchLocation = async () => {
      try {
        const response = await axios.get('https://ipapi.co/json/');
        setUserLocation(response.data);
      } catch (error) {
        setError('Error fetching location');
        console.error('Location Error:', error);
      }
    };

    const fetchHadith = async () => {
      const url = `https://cdn.jsdelivr.net/gh/fawazahmed0/hadith-api@1/editions/eng-abudawud/1035.json`;

      try {
        const response = await axios.get(url);
        let hadithText = '';
        if (response.data.hadiths && response.data.hadiths.length > 0) {
          hadithText = response.data.hadiths[0].text;
        } else if (response.data.hadith) {
          hadithText = response.data.hadith;
        } else {
          hadithText = 'No hadith found';
        }

        const translatedResponse = await axios.post('http://localhost:5000/translate', {
          text: hadithText,
          target_lang: language
        });
        setHadith(translatedResponse.data.text);
      } catch (error) {
        setError('Error fetching hadith');
        console.error('Error fetching hadith:', error);
      }
    };

    fetchDailyVerse();
    fetchLocation();
    fetchHadith();
  }, [language]);

  useEffect(() => {
    const fetchPrayerTimes = async (city, country) => {
      try {
        const response = await axios.get(`http://api.aladhan.com/v1/timingsByCity?city=${city}&country=${country}&method=2`);
        setPrayerTimes(response.data.data.timings);
      } catch (error) {
        setError('Error fetching prayer times');
        console.error('Prayer Times Error:', error);
      }
    };

    if (userLocation) {
      fetchPrayerTimes(userLocation.city, userLocation.country_name);
    }
  }, [userLocation]);

  const t = translations[language] || translations.en;

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentInsetAdjustmentBehavior="automatic">
        <View style={styles.header}>
          <Text style={styles.welcome}>{t.welcome}</Text>
          <Picker
            selectedValue={language}
            style={styles.languagePicker}
            onValueChange={(itemValue) => setLanguage(itemValue)}
          >
            <Picker.Item label="English" value="en" />
            <Picker.Item label="Türkçe" value="tr" />
            <Picker.Item label="العربية" value="ar" />
            <Picker.Item label="Français" value="fr" />
            <Picker.Item label="Deutsch" value="de" />
            <Picker.Item label="Español" value="es" />
            <Picker.Item label="Русский" value="ru" />
            <Picker.Item label="Azərbaycan" value="az" />
            <Picker.Item label="Қазақ" value="kk" />
            <Picker.Item label="中文" value="zh" />
          </Picker>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>{t.dailyVerse}</Text>
          <Text style={styles.verseText}>{originalVerse}</Text>
          <Text style={styles.verseText}>{translatedVerse}</Text>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>{t.prayerTimes}</Text>
          {userLocation && (
            <Text style={styles.locationText}>{userLocation.city}, {userLocation.country_name}</Text>
          )}
          {prayerTimes && (
            <View style={styles.prayerTimesContainer}>
              {Object.entries(prayerTimes).map(([name, time]) => (
                <View key={name} style={styles.prayerTime}>
                  <Text style={styles.prayerName}>{name}</Text>
                  <Text style={styles.prayerTimeText}>{time}</Text>
                </View>
              ))}
            </View>
          )}
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>{t.randomHadith}</Text>
          <Text style={styles.hadithText}>{hadith}</Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ffffff',
  },
  header: {
    padding: 20,
    alignItems: 'center',
  },
  welcome: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  languagePicker: {
    height: 50,
    width: 150,
  },
  section: {
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#cccccc',
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  verseText: {
    fontSize: 16,
    color: '#333333',
  },
  locationText: {
    fontSize: 16,
    color: '#333333',
    marginBottom: 10,
  },
  prayerTimesContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  prayerTime: {
    width: '48%',
    padding: 10,
    backgroundColor: '#f9f9f9',
    marginBottom: 10,
    borderRadius: 5,
    alignItems: 'center',
  },
  prayerName: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  prayerTimeText: {
    fontSize: 16,
    color: '#333333',
  },
  hadithText: {
    fontSize: 16,
    color: '#333333',
  },
});
