// src/TonWallet.js
import React, { useEffect, useState } from 'react';
import { TonConnect, Wallet } from '@tonconnect/sdk';

const tonConnect = new TonConnect();

const TonWallet = () => {
  const [wallet, setWallet] = useState(null);
  const [address, setAddress] = useState('');
  const [balance, setBalance] = useState('');

  useEffect(() => {
    // Bağlı cüzdanı al
    const connectedWallet = tonConnect.getWallet();
    setWallet(connectedWallet);
    if (connectedWallet) {
      // Cüzdan adresini ve bakiyesini al
      setAddress(connectedWallet.account.address);
      connectedWallet.getBalance().then(setBalance);
    }
  }, []);

  const connectWallet = async () => {
    try {
      const wallet = await tonConnect.connect();
      setWallet(wallet);
      setAddress(wallet.account.address);
      const balance = await wallet.getBalance();
      setBalance(balance);
    } catch (error) {
      console.error('Ton Wallet bağlantısı başarısız:', error);
    }
  };

  const disconnectWallet = () => {
    tonConnect.disconnect();
    setWallet(null);
    setAddress('');
    setBalance('');
  };

  return (
    <div>
      {wallet ? (
        <div>
          <p>Bağlı cüzdan adresi: {address}</p>
          <p>Bakiyeniz: {balance}</p>
          <button onClick={disconnectWallet}>Cüzdanı Bağlantısını Kes</button>
        </div>
      ) : (
        <button onClick={connectWallet}>Ton Wallet ile Giriş Yap</button>
      )}
    </div>
  );
};

export default handleWalletConnection;
