import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Kayit.css';

const languages = {
  en: 'English',
  ru: 'русский',
  tr: 'Türkçe',
  fr: 'Français',
  ar: 'العربية',
  zh: '中文',
  de: 'Deutsch',
  kk: 'Қазақ',
  az: 'Azərbaycan'
};

const languagePrompts = {
  en: 'Choose your language',
  ru: 'Выберите ваш язык',
  tr: 'Dil Seçiniz',
  fr: 'Choisissez votre langue',
  ar: 'اختر لغتك',
  zh: '选择你的语言',
  de: 'Wähle deine Sprache',
  kk: 'Тіліңізді таңдаңыз',
  az: 'Dil seçin'
};

const Kayit = ({ onSelectLanguage }) => {
  const [currentLanguage, setCurrentLanguage] = useState(languagePrompts.en);
  const languageKeys = Object.keys(languages);
  let languageIndex = 0;
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLanguage(languagePrompts[languageKeys[languageIndex]]);
      languageIndex = (languageIndex + 1) % languageKeys.length;
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const handleLanguageSelect = (language) => {
    localStorage.setItem('selectedLanguage', language);
    onSelectLanguage();
    navigate('/wallet'); // Kayıt işleminden sonra cüzdan bağlantı sayfasına yönlendir
  };

  const isMobile = window.innerWidth <= 768;
  const backgroundImage = isMobile ? '/kayit/Kayıt.png' : '/kayit/Arkaplan.png';

  const containerStyle = {
    width: '100%',
    height: '100vh',
    position: 'relative',
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column',
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center',
    padding: '10px',
    overflow: 'hidden'
  };

  return (
    <div style={containerStyle}>
      <img src="/logo.png" alt="Logo" className="logo" />
      <div className="current-language">{currentLanguage}</div>
      <div className="language-container">
        {languageKeys.map((key) => (
          <button
            key={key}
            className="language-button"
            onClick={() => handleLanguageSelect(key)}
          >
            <span className="language-text">{languages[key]}</span>
          </button>
        ))}
      </div>
    </div>
  );
};

export default Kayit;
