import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const SurahList = () => {
  const [surahs, setSurahs] = useState([]);

  useEffect(() => {
    axios.get('https://api.quran.com/api/v4/chapters')
      .then(response => {
        console.log(response.data.chapters);  // Gelen yanıtı kontrol edelim
        setSurahs(response.data.chapters);
      })
      .catch(error => {
        console.error('Sureler alınırken hata oluştu!', error);
      });
  }, []);

  return (
    <div>
      <h1>Kuran Sureleri</h1>
      <ul>
        {surahs.map(surah => (
          <li key={surah.id}>
            <Link to={`/surah/${surah.id}`}>{surah.name_simple}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SurahList;
