import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './WalletConnector.css'; // CSS dosyasını import ediyoruz

// Örnek tonConnect fonksiyonunu buraya ekleyelim
const tonConnect = async () => {
  // Bu, tonConnect fonksiyonunun basit bir simülasyonudur.
  // Gerçek cüzdan bağlantı kodunuzu buraya eklemeniz gerekir.
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({ address: '0:exampleaddress' });
    }, 1000);
  });
};

const WalletConnector = ({ onConnectWallet }) => {
  const [isConnected, setIsConnected] = useState(false);
  const navigate = useNavigate();

  const connectWallet = async () => {
    try {
      // Cüzdan bağlama kodu buraya gelecek (TON cüzdan entegrasyonu)
      // Örnek olarak:
      const wallet = await tonConnect();
      setIsConnected(true);
      onConnectWallet(wallet);
      navigate('/MainPage'); // Başarılı bağlantıdan sonra ana sayfaya yönlendir
    } catch (error) {
      console.error('Wallet connection failed', error);
    }
  };

  return (
    <div className="wallet-connector slide-in-bottom">
      {!isConnected ? (
        <button className="connect-button" onClick={connectWallet}>
          Connect Wallet
        </button>
      ) : (
        <p className="connected-message">Wallet Connected!</p>
      )}
    </div>
  );
};

export default WalletConnector;
