import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MainPage.css';

const MainPage = () => {
  const [verse, setVerse] = useState(null);
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [location, setLocation] = useState(null);

  useEffect(() => {
    const fetchDailyVerse = async () => {
      try {
        const response = await axios.get('https://api.quran.com/api/v4/verses/random?language=tr');
        setVerse(response.data);
      } catch (error) {
        console.error('Ayet alınırken hata oluştu!', error);
      }
    };

    const fetchLocation = async () => {
      try {
        const response = await axios.get('https://ipapi.co/json/');
        setLocation(response.data);
      } catch (error) {
        console.error('Konum alınırken hata oluştu!', error);
      }
    };

    const fetchPrayerTimes = async () => {
      if (location) {
        try {
          const response = await axios.get(`https://api.diyanet.gov.tr/v1/namazvakitleri?il=${location.city}`);
          setPrayerTimes(response.data);
        } catch (error) {
          console.error('Namaz vakitleri alınırken hata oluştu!', error);
        }
      }
    };

    fetchDailyVerse();
    fetchLocation();
    fetchPrayerTimes();
  }, [location]);

  return (
    <div className="main-page">
      <div className="daily-verse">
        <h2>Günün Ayeti</h2>
        {verse ? <p>{verse.text}</p> : <p>Yükleniyor...</p>}
      </div>
      <div className="prayer-times">
        <h2>Namaz Vakitleri</h2>
        {prayerTimes ? (
          <ul>
            {prayerTimes.map((time, index) => (
              <li key={index}>{time.name}: {time.time}</li>
            ))}
          </ul>
        ) : <p>Yükleniyor...</p>}
      </div>
      {/* Günlük dualar ve hadisler gibi diğer bölümler */}
    </div>
  );
};

export default MainPage;
