import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useLocation } from 'react-router-dom';

const translationEndpoints = {
  en: 131, // English translation ID
  fr: 133, // French translation ID
  ru: 134, // Russian translation ID
  tr: 'tr.altinkaya' // Turkish translation ID
};

const getTranslationUrl = (surahNumber, language) => {
  if (language === 'tr') {
    return `https://api.acikkuran.com/surah/${surahNumber}`;
  } else {
    const translationId = translationEndpoints[language];
    return `https://api.quran.com/api/v4/quran/translations/${translationId}?chapter_number=${surahNumber}`;
  }
};

const SurahTranslation = () => {
  const { surahNumber } = useParams();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const language = queryParams.get('language');
  const [surah, setSurah] = useState(null);
  const [ayahs, setAyahs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSurahDetails = async () => {
      try {
        let surahResponse, ayahsResponse;

        if (language === 'tr') {
          surahResponse = await axios.get(`https://api.acikkuran.com/surah/${surahNumber}`);
          setSurah(surahResponse.data.data);
          setAyahs(surahResponse.data.data.verses);
        } else {
          surahResponse = await axios.get(`https://api.quran.com/api/v4/chapters/${surahNumber}`);
          setSurah(surahResponse.data.chapter);

          ayahsResponse = await axios.get(getTranslationUrl(surahNumber, language));
          setAyahs(ayahsResponse.data.translations);
        }

        setLoading(false);
      } catch (error) {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
      }
    };

    fetchSurahDetails();
  }, [surahNumber, language]);

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  return (
    <div>
      <h1>{surah.name_simple || surah.name}</h1>
      <p>{surah.translated_name?.name || surah.name}</p>
      <ul>
        {ayahs.map(ayah => (
          <li key={ayah.verse_key || ayah.verseNumber}>
            <p>{ayah.verse_key || ayah.verseNumber}</p>
            <p>{ayah.text || ayah.verse}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SurahTranslation;
