import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Dhikr.css';

const translations = {
  tr: {
    count: "Sayı",
  },
  ar: {
    count: "عدد",
  },
  fr: {
    count: "Nombre",
  },
  en: {
    count: "Count",
  },
  ru: {
    count: "Число",
  },
  zh: {
    count: "计数",
  },
  de: {
    count: "Anzahl",
  },
  kk: {
    count: "Саны",
  },
  az: {
    count: "Say",
  }
};

const Dhikr = () => {
  const location = useLocation();
  const { language } = location.state || { language: 'en' };
  const [count, setCount] = useState(0);

  const handleDhikrClick = () => {
    setCount(count + 1);
    if (navigator.vibrate) {
      navigator.vibrate(200); // Cihazı titret
    }
  };

  const styles = {
    container: {
      width: '100%',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: '#f0f0f0',
      fontFamily: 'Arial, sans-serif',
    },
    count: {
      fontSize: 48,
      fontWeight: 'bold',
      margin: 20,
    },
    button: {
      width: 150,
      height: 50,
      background: '#31934D',
      borderRadius: 12,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      border: 'none',
      cursor: 'pointer',
      color: 'white',
      fontSize: 18,
      transition: 'background 0.3s, box-shadow 0.3s',
      boxShadow: '0 8px 12px rgba(0, 0, 0, 0.2)',
    },
    buttonText: {
      fontSize: 18,
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.count}>
        {translations[language]?.count || "Count"}: {count}
      </div>
      <button style={styles.button} onClick={handleDhikrClick}>
        <span style={styles.buttonText}>Dhikr</span>
      </button>
    </div>
  );
};

export default Dhikr;
