import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const SurahList = () => {
  const [surahs, setSurahs] = useState([]);

  useEffect(() => {
    axios.get('https://cdn.jsdelivr.net/gh/fawazahmed0/quran-api@1/editions/ara-quran.json')
      .then(response => {
        setSurahs(response.data.quran);
      })
      .catch(error => {
        console.error('Sureler alınırken hata oluştu!', error);
      });
  }, []);

  return (
    <div>
      <h1>Kuran Sureleri</h1>
      <ul>
        {surahs.map((surah, index) => (
          <li key={index}>
            <Link to={`/surah/${index + 1}`}>{surah.name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SurahList;
