import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import './MainPage.css';

const MainPage = ({ language }) => {
  const [originalVerse, setOriginalVerse] = useState(null);
  const [translatedVerse, setTranslatedVerse] = useState(null);
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [dua, setDua] = useState(null);
  const [hadith, setHadith] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [error, setError] = useState(null);
  const [scrollY, setScrollY] = useState(0);

  const languageMap = {
    en: 'English',
    tr: 'Türkçe',
    ar: 'العربية',
    fr: 'Français',
    de: 'Deutsch',
    es: 'Español',
    ru: 'русский',
    az: 'Azərbaycan',
    kk: 'Қазақ',
    zh: '中文'
  };

  const translations = {
    en: {
      welcome: 'Welcome',
      selectLanguage: 'Select Language',
      dailyVerse: 'Daily Verse',
      prayerTimes: 'Prayer Times',
      randomDua: 'Random Dua',
      randomHadith: 'Random Hadith'
    },
    tr: {
      welcome: 'Hoş Geldiniz',
      selectLanguage: 'Dil Seçiniz',
      dailyVerse: 'Günün Ayeti',
      prayerTimes: 'Namaz Vakitleri',
      randomDua: 'Rastgele Dua',
      randomHadith: 'Rastgele Hadis'
    },
    ar: {
      welcome: 'أهلا بك',
      selectLanguage: 'اختر لغتك',
      dailyVerse: 'آية اليوم',
      prayerTimes: 'مواقيت الصلاة',
      randomDua: 'دعاء عشوائي',
      randomHadith: 'حديث عشوائي'
    },
    fr: {
      welcome: 'Bienvenue',
      selectLanguage: 'Choisissez votre langue',
      dailyVerse: 'Verset du jour',
      prayerTimes: 'Heures de prière',
      randomDua: 'Dua aléatoire',
      randomHadith: 'Hadith aléatoire'
    },
    de: {
      welcome: 'Willkommen',
      selectLanguage: 'Sprache wählen',
      dailyVerse: 'Täglicher Vers',
      prayerTimes: 'Gebetszeiten',
      randomDua: 'Zufälliges Dua',
      randomHadith: 'Zufälliges Hadith'
    },
    es: {
      welcome: 'Bienvenido',
      selectLanguage: 'Elige tu idioma',
      dailyVerse: 'Verso del día',
      prayerTimes: 'Tiempos de oración',
      randomDua: 'Dua aleatoria',
      randomHadith: 'Hadith aleatorio'
    },
    ru: {
      welcome: 'Добро пожаловать',
      selectLanguage: 'Выберите ваш язык',
      dailyVerse: 'Ежедневный стих',
      prayerTimes: 'Время намаза',
      randomDua: 'Случайная дуа',
      randomHadith: 'Случайный хадис'
    },
    az: {
      welcome: 'Xoş gəldiniz',
      selectLanguage: 'Dil seçin',
      dailyVerse: 'Günün Ayəsi',
      prayerTimes: 'Namaz Vaxtları',
      randomDua: 'Təsadüfi Dua',
      randomHadith: 'Təsadüfi Hədis'
    },
    kk: {
      welcome: 'Қош келдіңіз',
      selectLanguage: 'Тіліңізді таңдаңыз',
      dailyVerse: 'Күнделікті аят',
      prayerTimes: 'Намаз уақыттары',
      randomDua: 'Кездейсоқ Дуа',
      randomHadith: 'Кездейсоқ Хадис'
    },
    zh: {
      welcome: '欢迎',
      selectLanguage: '选择你的语言',
      dailyVerse: '每日经文',
      prayerTimes: '祷告时间',
      randomDua: '随机祷告',
      randomHadith: '随机圣训'
    }
  };

  useEffect(() => {
    const fetchDailyVerse = async () => {
      try {
        const response = await axios.get('https://api.alquran.cloud/v1/ayah/random/ar');
        const verseText = response.data.data.text;
        setOriginalVerse(verseText);

        const translatedResponse = await axios.post(`https://translation.googleapis.com/language/translate/v2`, {
          q: verseText,
          target: language,
          key: 'YOUR_GOOGLE_TRANSLATE_API_KEY'
        });
        setTranslatedVerse(translatedResponse.data.data.translations[0].translatedText);
      } catch (error) {
        setError('Error fetching verse or translation');
        console.error('Verse/Translation Error:', error);
      }
    };

    const fetchLocation = async () => {
      try {
        const response = await axios.get('https://ipapi.co/json/');
        setUserLocation(response.data);
      } catch (error) {
        setError('Error fetching location');
        console.error('Location Error:', error);
      }
    };

    const fetchDua = async () => {
      try {
        const response = await axios.get('https://islamic-api-abr.herokuapp.com/dua');
        console.log('Dua Response:', response.data);
        setDua(response.data.data || 'N/A');
      } catch (error) {
        setError('Error fetching dua');
        console.error('Error fetching dua:', error);
      }
    };

    const fetchHadith = async () => {
      const languageMap = {
        en: 'eng',
        tr: 'tur',
        ar: 'ara',
        ru: 'rus'
      };
      const langCode = languageMap[language] || 'eng';

      const url = `https://cdn.jsdelivr.net/gh/fawazahmed0/hadith-api@1/editions/${langCode}-abudawud/1035.json`;

      try {
        const response = await axios.get(url);
        console.log('Hadith Response:', response.data);
        if (response.data.hadiths && response.data.hadiths.length > 0) {
          setHadith(response.data.hadiths[0].text);
        } else if (response.data.hadith) {
          setHadith(response.data.hadith);
        } else {
          setHadith('No hadith found');
        }
      } catch (error) {
        setError('Error fetching hadith');
        console.error('Error fetching hadith:', error);
      }
    };

    fetchDailyVerse();
    fetchLocation();
    fetchDua();
    if (['en', 'tr', 'ru'].includes(language)) {
      fetchHadith();
    }
  }, [language]);

  useEffect(() => {
    const fetchPrayerTimes = async (city, country) => {
      try {
        const response = await axios.get(`http://api.aladhan.com/v1/timingsByCity?city=${city}&country=${country}&method=2`);
        setPrayerTimes(response.data.data.timings);
      } catch (error) {
        setError('Error fetching prayer times');
        console.error('Prayer Times Error:', error);
      }
    };

    if (userLocation) {
      fetchPrayerTimes(userLocation.city, userLocation.country_name);
    }
  }, [userLocation]);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="main-page" style={{ backgroundImage: 'url("/public/kayit/MainPage.png")', backgroundSize: 'cover' }}>
      {error && <p>{error}</p>}
      <div className="welcome-message">
        <h1>{translations[language].welcome}</h1>
      </div>
      <div className="profile-menu">
        <Link to="/profile">
          <img src="/icons/profile.png" alt="Profile" className="profile-icon" />
        </Link>
      </div>
      <div className="language-selector">
        <label htmlFor="language">{translations[language].selectLanguage}: </label>
        <select id="language" value={language} readOnly>
          {Object.keys(languageMap).map((lang) => (
            <option key={lang} value={lang}>
              {languageMap[lang]}
            </option>
          ))}
        </select>
      </div>
      <div className="daily-verse">
        <h2>{translations[language].dailyVerse}</h2>
        {originalVerse && <p>{originalVerse}</p>}
        {translatedVerse && <p>{translatedVerse}</p>}
      </div>
      <div className="prayer-times">
        <h2>{translations[language].prayerTimes}</h2>
        {userLocation && <p>{userLocation.city}, {userLocation.country_name} - {new Date().toLocaleTimeString()}</p>}
        {prayerTimes && (
          <div className="scrollable-prayer-times">
            {Object.entries(prayerTimes).map(([name, time]) => (
              <button key={name} className="prayer-time-button">{name}: {time}</button>
            ))}
          </div>
        )}
      </div>
      <div className="dua">
        <h2>{translations[language].randomDua}</h2>
        {dua && <p>{dua}</p>}
      </div>
      {['en', 'tr', 'ru'].includes(language) && (
        <div className="hadith">
          <h2>{translations[language].randomHadith}</h2>
          {hadith && <p>{hadith}</p>}
        </div>
      )}
      <Navbar className={scrollY > 0 ? 'scrolled' : ''} />
    </div>
  );
};

export default MainPage;
