// src/components/WalletConnector.js
import React, { useEffect, useState } from 'react';
import TonConnect, { Wallet } from '@tonconnect/sdk';

const WalletConnector = ({ onConnectWallet }) => {
  const [wallet, setWallet] = useState(null);
  const [address, setAddress] = useState('');
  const [balance, setBalance] = useState('');

  const tonConnect = new TonConnect();

  useEffect(() => {
    // Check if a wallet is already connected
    const storedWallet = localStorage.getItem('wallet');
    if (storedWallet) {
      const walletData = JSON.parse(storedWallet);
      setWallet(walletData);
      setAddress(walletData.account.address);
      setBalance(walletData.balance);
    }
  }, []);

  const connectWallet = async () => {
    try {
      const manifestUrl = 'https://your-manifest-url'; // Replace with your manifest URL
      const walletConnectionSource = {
        jsBridgeKey: 'tonkeeper', // You might need to specify the correct wallet here
        name: 'Tonkeeper'
      };
      const wallet = await tonConnect.connect({
        manifestUrl,
        walletConnectionSource
      });
      const walletData = {
        account: wallet.account,
        balance: await wallet.getBalance(),
      };
      setWallet(wallet);
      setAddress(wallet.account.address);
      setBalance(walletData.balance);
      localStorage.setItem('wallet', JSON.stringify(walletData));
      onConnectWallet();
    } catch (error) {
      console.error('Ton Wallet connection failed:', error);
    }
  };

  const disconnectWallet = () => {
    tonConnect.disconnect();
    setWallet(null);
    setAddress('');
    setBalance('');
    localStorage.removeItem('wallet');
  };

  return (
    <div>
      {wallet ? (
        <div>
          <p>Connected wallet address: {address}</p>
          <p>Your balance: {balance}</p>
          <button onClick={disconnectWallet}>Disconnect Wallet</button>
        </div>
      ) : (
        <button onClick={connectWallet}>Connect with Ton Wallet</button>
      )}
    </div>
  );
};

export default WalletConnector;
