import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './WalletConnector.css';

const translations = {
  tr: {
    greeting: "Merhaba",
    connect: "Ton ile Bağlan"
  },
  ar: {
    greeting: "مرحبا",
    connect: "الاتصال بـ Ton"
  },
  fr: {
    greeting: "Bonjour",
    connect: "Se connecter avec Ton"
  },
  en: {
    greeting: "Hello",
    connect: "Connect with Ton"
  },
  ru: {
    greeting: "Здравствуйте",
    connect: "Подключиться к Ton"
  },
  zh: {
    greeting: "你好",
    connect: "连接 Ton"
  },
  de: {
    greeting: "Hallo",
    connect: "Mit Ton verbinden"
  },
  kk: {
    greeting: "Сәлеметсіз бе",
    connect: "Ton-мен қосылу"
  },
  az: {
    greeting: "Salam",
    connect: "Ton ilə bağlan"
  }
};

const WalletConnector = ({ onConnectWallet }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const { language } = location.state || { language: 'en' };
  const [walletConnected, setWalletConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState('');

  const connectWallet = async () => {
    if (window.ton) {
      try {
        const accounts = await window.ton.send('ton_requestAccounts');
        if (accounts.length > 0) {
          setWalletAddress(accounts[0]);
          setWalletConnected(true);
          onConnectWallet();
          navigate('/main'); // Cüzdan bağlantısından sonra ana sayfaya yönlendir
        }
      } catch (error) {
        console.error("Cüzdan bağlantısı başarısız", error);
      }
    } else {
      alert('TON Wallet uzantısını kurun');
    }
  };

  const styles = {
    container: {
      width: '100%',
      height: '100vh',
      position: 'relative',
      backgroundImage: 'url(/kayit/Kayıt.png)', // Kayıt sayfasının arka planını kullanıyoruz
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
    },
    message: {
      fontSize: 18,
      fontWeight: 'bold',
      color: 'white',
      animation: 'slideIn 0.5s forwards',
      fontFamily: 'Arial, sans-serif',
    },
    connectButton: {
      width: 150,
      height: 35,
      marginTop: 100,
      background: 'rgba(255, 255, 255, 0.90)',
      borderRadius: 12,
      overflow: 'hidden',
      justifyContent: 'center',
      alignItems: 'center',
      display: 'flex',
      border: 'none',
      cursor: 'pointer',
      transition: 'background 0.3s, box-shadow 0.3s',
      boxShadow: '0 8px 12px rgba(0, 0, 0, 0.2)',
      position: 'relative',
      animation: 'slideInFromBottom 0.5s forwards',
    },
    buttonText: {
      marginRight: 10,
    },
    walletInfo: {
      marginTop: 10,
      color: 'white',
      fontSize: 14,
      animation: 'slideIn 0.5s forwards',
    }
  };

  return (
    <div style={styles.container}>
      <style>{`
        @keyframes slideIn {
          from {
            transform: translateY(-100%);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }

        @keyframes slideInFromBottom {
          from {
            transform: translateY(100%);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }

        .slide-in {
          animation: slideIn 0.5s forwards;
        }

        .slide-in-bottom {
          animation: slideInFromBottom 0.5s forwards;
        }

        button:hover {
          background: rgba(255, 255, 255, 1);
          box-shadow: 0 12px 16px rgba(0, 0, 0, 0.2);
        }
      `}</style>
      <div style={styles.message} className="slide-in">{translations[language]?.greeting || "Hello"}</div>
      <button style={styles.connectButton} className="slide-in-bottom" onClick={connectWallet}>
        <span style={styles.buttonText}>{translations[language]?.connect || "Connect with Ton"}</span>
        <img src="/ton-logo.png" alt="Ton Logo" style={{ width: 20, height: 20 }} />
      </button>
      {walletConnected && (
        <div style={styles.walletInfo} className="slide-in">
          Wallet Connected: {walletAddress}
        </div>
      )}
    </div>
  );
};

export default WalletConnector;
