import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useLocation } from 'react-router-dom';

const translationEndpoints = {
  en: 'en.asad',
  fr: 'fr.hamidullah',
  ru: 'ru.kuliev',
  tr: 'tr.altinkaya'
};

const getTranslationUrl = (surahNumber, language) => {
  if (language === 'tr') {
    return `https://api.acikkuran.com/surah/${surahNumber}`;
  } else {
    const translationEndpoint = translationEndpoints[language];
    return `https://api.alquran.cloud/v1/surah/${surahNumber}/${translationEndpoint}`;
  }
};

const SurahTranslation = () => {
  const { surahNumber } = useParams();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const language = queryParams.get('language');
  const [surah, setSurah] = useState(null);
  const [ayahs, setAyahs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSurahDetails = async () => {
      try {
        let surahResponse, ayahsResponse;

        if (language === 'tr') {
          surahResponse = await axios.get(`https://api.acikkuran.com/surah/${surahNumber}`);
          ayahsResponse = surahResponse;
        } else {
          surahResponse = await axios.get(`https://api.alquran.cloud/v1/surah/${surahNumber}`);
          const translationUrl = getTranslationUrl(surahNumber, language);
          ayahsResponse = await axios.get(translationUrl);
        }

        setSurah(surahResponse.data.data);
        if (language === 'tr') {
          setAyahs(surahResponse.data.data.verses);
        } else {
          setAyahs(ayahsResponse.data.data.ayahs);
        }

        setLoading(false);
      } catch (error) {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
      }
    };

    fetchSurahDetails();
  }, [surahNumber, language]);

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  return (
    <div>
      <h1>{surah.englishName || surah.name}</h1>
      <p>{surah.englishNameTranslation || surah.name}</p>
      <ul>
        {ayahs.map(ayah => (
          <li key={ayah.numberInSurah || ayah.verseNumber}>
            <p>{ayah.numberInSurah || ayah.verseNumber}</p>
            <p>{ayah.text || ayah.verse}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SurahTranslation;
