import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './SurahList2.css'; // Stil dosyasını import edin

const SurahList2 = () => {
  const [surahs, setSurahs] = useState([]);
  const navigate = useNavigate();
  const selectedLanguage = localStorage.getItem('selectedLanguage') || 'tr';

  useEffect(() => {
    axios.get('https://api.quran.com/api/v4/chapters')
      .then(response => {
        setSurahs(response.data.chapters);
      })
      .catch(error => {
        console.error('Sureler alınırken hata oluştu!', error);
      });
  }, []);

  const handleSelection = (surahNumber) => {
    navigate(`/surah-translation/${surahNumber}?language=${selectedLanguage}`);
  };

  return (
    <div className="container">
      <h1 className="title">Kuran Sureleri</h1>
      <ul className="surah-list">
        {surahs.map(surah => (
          <li key={surah.id} className="surah-item">
            <span className="surah-name">{surah.name_simple}</span>
            <button className="surah-button" onClick={() => handleSelection(surah.id)}>Meal</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SurahList2;
