import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const SurahDetail = () => {
  const { surahNumber } = useParams();
  const [surah, setSurah] = useState(null);
  const [ayahs, setAyahs] = useState([]);
  const [translation, setTranslation] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentAyah, setCurrentAyah] = useState(null);
  const [audioError, setAudioError] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('en');

  useEffect(() => {
    const fetchSurahDetails = async () => {
      try {
        const surahResponse = await axios.get(`https://api.quran.com/api/v4/chapters/${surahNumber}`);
        setSurah(surahResponse.data.chapter);

        const ayahsResponse = await axios.get(`https://api.quran.com/api/v4/quran/verses/by_chapter/${surahNumber}`);
        setAyahs(ayahsResponse.data.verses);

        const translationResponse = await axios.get(`https://api.quran.com/api/v4/quran/translations/${selectedLanguage}/by_chapter/${surahNumber}`);
        setTranslation(translationResponse.data.translations);

        setLoading(false);
      } catch (error) {
        console.error('Sure bilgileri alınırken hata oluştu!', error);
        setLoading(false);
      }
    };

    fetchSurahDetails();
  }, [surahNumber, selectedLanguage]);

  const playAyahAudio = (ayahNumber) => {
    setAudioError(false);
    axios.get(`https://api.quran.com/api/v4/quran/verses/by_key/${surahNumber}:${ayahNumber}?audio=1`)
      .then(response => {
        const audioUrl = response.data.verses[0]?.audio.url;
        if (audioUrl) {
          setCurrentAyah(audioUrl);
        } else {
          setAudioError(true);
        }
      })
      .catch(error => {
        console.error('Ses dosyası alınırken hata oluştu!', error);
        setAudioError(true);
      });
  };

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  if (!surah) {
    return <p>Sure bulunamadı</p>;
  }

  const handleLanguageChange = (e) => {
    setSelectedLanguage(e.target.value);
  };

  return (
    <div>
      <h1>{surah.name_simple}</h1>
      <p>{surah.translated_name.name}</p>
      <div>
        <label htmlFor="language">Dil Seçimi: </label>
        <select id="language" value={selectedLanguage} onChange={handleLanguageChange}>
          <option value="en">İngilizce</option>
          <option value="tr">Türkçe</option>
          <option value="fr">Fransızca</option>
          <option value="de">Almanca</option>
          {/* Diğer diller burada eklenebilir */}
        </select>
      </div>
      <ul>
        {ayahs.map((ayah, index) => (
          <li key={ayah.verse_key}>
            {ayah.text_uthmani}
            <button onClick={() => playAyahAudio(ayah.verse_number)}>Ses Dosyasını Çal</button>
            {translation[index] && (
              <p>{translation[index].text}</p>
            )}
          </li>
        ))}
      </ul>
      {audioError && <p>Bu ayet için ses dosyası bulunamadı.</p>}
      {currentAyah && (
        <AudioPlayer
          autoPlay
          src={currentAyah}
          onEnded={() => setCurrentAyah(null)}
        />
      )}
    </div>
  );
};

export default SurahDetail;
