import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AllahIsimleri.css';

const allahNames = [
  { frame: 'Frame#1', name: 'Ar-Rahman', meaning: 'The Beneficent' },
  // Diğer isimler burada olabilir
];

const AllahIsimleri = () => {
  const [translatedName, setTranslatedName] = useState(allahNames[0]);

  useEffect(() => {
    const translateName = async () => {
      try {
        const response = await axios.post('http://localhost:5000/translate', {
          text: allahNames[0].meaning,
          target_lang: 'tr', // Türkçe'ye çevir
        });
        setTranslatedName({
          ...allahNames[0],
          translatedMeaning: response.data.text,
        });
      } catch (error) {
        console.error('Translation Error:', error);
      }
    };

    translateName();
  }, []);

  return (
    <div className="allah-isimleri-page">
      <div className="sidebar">
        <img src="/allahisimleri/Frame#1.png" alt="Logo" className="logo" />
      </div>
      <div className="name-item">
        <h3>{translatedName.frame} - {translatedName.name}</h3>
        <p>{translatedName.translatedMeaning ? translatedName.translatedMeaning : translatedName.meaning}</p>
      </div>
    </div>
  );
};

export default AllahIsimleri;
